#include <iostream>
#include <fstream>
#include <map>

// Solution: Q1
long MeterReader (char *array){

    if (array == NULL) return 0;
    
    long visitedHouses = 1;
    long x = 0;
    long y = 0;

    std::map<long, long> locations;
    locations.insert(x,y);



    char *prt = array;
    
    while (prt!= NULL && *prt!= '\n'){
        if (*prt == '^')
        locations.insert(x,++y);
        
        if (*prt == 'v')
        locations.insert(x,--y);
        
        if (*prt == '>')
        locations.insert(++x,y);
        
        if (*prt == '<')
        locations.insert(--x,y);
        
        prt++;
    }
    return visitedHouses;
    
}

