#include <iostream>
#include <fstream>

long FindFloor(char *array){
    if (array == NULL) return 0;
    char *prt = array;
    long floor = 0;
    while (prt!= NULL && *prt!= '\n'){
        if (*prt == '(')
        floor+=1;
        if (*prt == ')')
        floor-=1;
        prt++;
    }
    return floor;
    
}

